    @Override
    public List<OptionsPanel.PathItem> getPath() {
        ArrayList<OptionsPanel.PathItem> path = new ArrayList<>();
        path.add(new PathItem("start", resourceBundle.getString("options.Path.0")));
        return path;
    }

    @Override
    public void loadFromPreferences(Preferences preferences) {
        checkForUpdatesOnStartCheckBox.setSelected(Boolean.parseBoolean(preferences.get(PREFERENCES_CHECK_FOR_UPDATE_ON_START, Boolean.TRUE.toString())));
    }

    @Override
    public void saveToPreferences(Preferences preferences) {
        preferences.put(PREFERENCES_CHECK_FOR_UPDATE_ON_START, Boolean.toString(checkForUpdatesOnStartCheckBox.isSelected()));
    }

    @Override
    public void applyPreferencesChanges() {
    }

    private void setModified(boolean b) {
        if (modifiedOptionListener != null) {
            modifiedOptionListener.wasModified();
        }
    }

    @Override
    public void setModifiedOptionListener(ModifiedOptionListener listener) {
        modifiedOptionListener = listener;
    }
